mapper.select:
SELECT COUNT(*)
FROM Cart
WHERE CART_STUDENT_ID = #{studentId}
  AND CART_LECTURE_ID = #{lectureId}

mapper.insert:
INSERT INTO Cart (
    CART_STUDENT_ID,
    CART_LECTURE_ID
) VALUES (
             #{studentId},
             #{lectureId}
         )

mapper.delete:
DELETE FROM Cart
WHERE CART_STUDENT_ID = #{studentId}
  AND CART_LECTURE_ID = #{lectureId}
